import "../../style/carousel/Card.scss";

const CardTopic= ({ newbook, cover }) => {

    let cov = cover
    let image;
    console.log(cov);
    return(
        <div className="card">
            {cov =>(cov == 'cover_id')? 
            image = <img className="cardImg" src={`https://covers.openlibrary.org/b/id/${newbook?.cover_id}-M.jpg`} alt={newbook?.title}/>:
            image = <img className="cardImg" src={`https://covers.openlibrary.org/b/id/${newbook?.cover_i}-M.jpg`} alt={newbook?.title}/>
            }            
            <div>{image}</div>

            <div className="data">
                <p className="cardP">{newbook?.title}</p>
                <div className="author">
                    <p className="cardP">{newbook?.author_name}</p>
                    <p className="cardP">{newbook?.first_publish_year}</p>        
                </div>
            </div>
        </div>
    );
};

export default CardTopic;
