                            "@1.75":{
                                slidesPerView: 4,
                                spaceBetween:25,
                            },
                            /* "@2.00":{
                                slidesPerView: 5,
                                spaceBetween:25,
                            },
                            "@2.25":{
                                slidesPerView: 6,
                                spaceBetween:25,
                            },
                            "@2.50":{
                                slidesPerView: 7,
                                spaceBetween:25,
                            }, */

                        }}
                        >
                            {newbooks?.map((newbook) => (
                                <div>
                                    {console.log(newbook)}
                                <SwiperSlide>
                                    <CardTopic newbook={newbook}/>
                                </SwiperSlide>  
                                </div>
                            ))}

                        </Swiper>
                    </div>
                <div className="paginacion" />
            </div> 
        </>
    );
}

export default CarouselTopic;
